INSERT INTO `san_pham` (`ma_sp`, `ten_sp`, `gia_sp`, `giamgia`, `hinh`, `thongtin_ngan`, `thongtinchitiet`, `loai_sp`, `dong_goi`, `so_luong`, `luot_mua`, `hinh_chi_tiet`) VALUES
('sanpham01', 'Serum NNO Vite Aplicapz dưỡng trắng da hộp 30 viên', '99999', 15, 'https://cdn.tgdd.vn/Products/Images/6562/219828/nno-vite-aplicapz-whitening-serum-hop-3vi-hinh-2.jpg', 'Công dụng: Dưỡng trắng da, đều màu da.\nThương hiệu: Mega We care (Thái Lan) \nNhà sản xuất: Mega Lifesciences\nNơi sản xuất: Thái Lan\nCách đóng gói: Hộp 3 vỉ x 10 viên\nThành phần chính: Dẫn xuất Vitamin C, Vitamin E, Bisabolol.\nLoại da phù hợp: Mọi loại da\nLưu ý khi sử dụng: Sử dụng ngoài da, tránh các vùng da bị tổn thương (vết thương hở, bị bỏng,...)', 'Thành phần\nMỗi viên nang mềm chứa: \n\nDẫn xuất Vitamin C (Ascorbyl tetraisopalmitate) 3,65mg; Bisabolol 0,37mg; Vitamin E thiên nhiên (Tocopheryl acetate) 0,37mg.\n\nCác thành phần khác: Cyclopentasiloxane, Dimethicone crosspolymer, Dimethicone, Fragrance, Gelatin, Glycerin, Màu vàng sunset (Cl 15985), Oxy sắt vàng (Cl 77492), Titanium dioxide, Methylparaben, Propylparaben, Nước tinh khiết.\n\nCông dụng\nHoạt chất trong mỗi viên nang mềm được bào chế với công nghệ tiên tiến mang lại hiệu quả trắng sáng vượt trội.\n\nTác động kép của hỗn hợp làm trắng da bao gồm dẫn xuất vitamin C, vitamin E và bisabolol định hướng tác động đến các đốm nâu do nguyên nhân ánh nắng mặt trời, do tuổi tác, sẹo mụn, lỗ chân lông sậm màu cho làn da sáng dần lên một cách hoàn hảo.\n\nDẫn xuất vitamin C có tác dụng ức chế hình thành melanin (hắc tố) giúp làn da sáng và đều màu, Vitamin E hoạt động như một chất chống oxy hóa giúp ngăn ngừa tác động của ánh nắng mặt trời và các chất gây ô nhiễm, Bisabolol là chất chống kích ứng, có nguồn gốc từ hoa Chamomile, giúp giảm kích ứng da và cải thiện tình trạng da. \n\nHướng dẫn sử dụng\nCách mở viên nang: Cho đầu viên nang hướng lên trên, xoắn nhẹ 2 vòng và ngắt mở viên nang.\n\nCách sử dụng: Bôi đều từ đầu ngón tay lên mặt và cổ mỗi đêm.\n\nLưu ý\nChỉ dùng ngoài da. Sản phẩm không gây kích ứng da.\n\nBảo quản\nNơi khô mát, tránh ánh nắng trực tiếp.\n\nQuy cách đóng gói\nHộp 3 vỉ x 10 viên nang mềm.\n\nNhà sản xuất\nMega Lifesciences.\n\nNước sản xuất\nThái Lan.\n\nDƯỠNG DA, DƯỠNG MÔI KHÁC', 'duong_da', 'Hộp 3 vỉ x 10 viên', 54, 0, 'https://cdn.tgdd.vn/Products/Images/6562/219828/nno-vite-aplicapz-whitening-serum-hop-3vi-hinh-2.jpg'),
('sanpham02', 'Kem Fixderma Preggers giúp ngừa rạn da, dưỡng ẩm dành cho bà bầu tuýp 60g', '380000', 10, 'https://cdn.tgdd.vn/Products/Images/6562/286943/kem-ngua-ran-da-fixderma-danh-cho-ba-bau-1.jpg', 'Công dụng: Giúp ngăn ngừa rạn da và làm mờ vết rạn cũ nhất là trong và sau khi sinh.\r\nThương hiệu: Fixderma (Mỹ)\r\nNhà sản xuất: Fixderma\r\nNơi sản xuất: Ấn Độ\r\nCách đóng gói: Tuýp 60g\r\nThành phần chính: Coffea Arabica (Coffee) Seed Oil, Brassica Campestris (Rapeseed) Sterols, Zingiber Officinale Extract, Hydrolysed Collagen,  Niacinamide (dẫn xuất vitamin B3), Butyrospermum Parkii (Shea butter), Dicaprylyl Carbonate, Lactic acid (alpha hydroxyl acid – AHA), Ceramide III, Sodium Hyaluronate.\r\nLoại da phù hợp: Mọi loại da\r\nLưu ý khi sử dụng: Nên dùng trong vòng 12 tháng sau khi mở nắp', 'Thành phần\r\nCoffea Arabica (Coffee) Seed Oil, Brassica Campestris (Rapeseed) Sterols, Zingiber Officinale Extract, Hydrolysed Collagen,  Niacinamide (dẫn xuất vitamin B3), Butyrospermum Parkii (Shea butter), Dicaprylyl Carbonate, Lactic acid (alpha hydroxyl acid – AHA), Ceramide III, Sodium Hyaluronate.\r\n\r\nCông dụng\r\nCải thiện tính đàn hồi, làm mềm và săn chắc da.\r\n\r\nGiúp ngăn ngừa rạn da và làm mờ vết rạn cũ nhất là trong và sau khi sinh.\r\n\r\nDưỡng ẩm, làm mờ sẹo, loại trừ các vết đỏ và giúp da mềm mịn.\r\n\r\nHướng dẫn sử dụng\r\nDùng ngón tay thoa kem theo vòng tròn.\r\n\r\nMát xa nhẹ nhàng cho kem thấm.\r\n\r\nDùng 2 lần mỗi ngày sau khi tắm và da còn ẩm.\r\n\r\nBảo quản\r\nGiữ sản phẩm ở nơi khô và mát dưới 30oC .\r\n\r\nTránh làm đông lạnh.\r\n\r\nLưu ý\r\nNgưng sử dụng và báo cho bác sĩ hoặc nhân viên y tế khi có xảy ra dị ứng với bất kỳ thành phần nào của sản phẩm.\r\n\r\nĐể xa tầm tay trẻ em.\r\n\r\nDƯỠNG DA, DƯỠNG MÔI KHÁC\r\n\r\n', 'duong_da', 'Tuýp 60g', 54, 23, 'https://cdn.nhathuoclongchau.com.vn/unsafe/447x447/https://cms-prod.s3-sgn09.fptcloud.com/00029094_kinder_optima_100ml_aktiv_9532_6256_large_eaf0735bf0.jpg,https://cdn.nhathuoclongchau.com.vn/unsafe/447x447/https://cms-prod.s3-sgn09.fptcloud.com/00029094_kinder_optima_100ml_aktiv_6002_6256_large_fe06205aa0.jpg,https://cdn.nhathuoclongchau.com.vn/unsafe/447x447/https://cms-prod.s3-sgn09.fptcloud.com/00029094_kinder_optima_100ml_aktiv_6265_6256_large_7750f4b5dd.jpg'),
('sanpham03', 'Tẩy tế bào chết Eucerin Dermo Purifyer ngừa mụn, giảm bóng nhờn tuýp 100ml', '389000', 12, 'https://cdn.tgdd.vn/Products/Images/6562/135375/tay-te-bao-chet-eucerin-100ml-1.jpg', 'Công dụng: Thông thoáng lỗ chân lông, giảm mụn và vết thâm do mụn.\r\nThương hiệu: Eucerin (Đức)\r\nNhà sản xuất: Eucerin\r\nNơi sản xuất: Đức\r\nCách đóng gói: Tuýp 100ml\r\nThành phần chính: Aqua, coco-glucoside, coco-betaine, microcrystalline cellulose, sodium acrylates/c10-30 alkyl acrylate crosspolymer, lactic acid, sodium chloride, cellulose, mannitol, polyquaternium-10, sodium benzoate, phenoxyethanol, benzophenone-4, parfum, ci 77289.\r\nLoại da phù hợp: Mọi loại da\r\nLưu ý khi sử dụng: Chỉ dùng ngoài da\r\nHướng dẫn bảo quản: Tránh ánh nắng trực tiếp hoặc nơi ẩm thấp', 'Đặc điểm nổi bật\r\nDùng cho da bị mụn và da dầu.\r\n\r\nLà sản phẩm tẩy tế bào chết không chứa dầu, kết hợp Lactic Acid giúp giảm mụn mà không làm khô da.\r\n\r\nCông dụng\r\nTẩy tế bào chết ngăn ngừa mụn Eucerin Dermo Purifyer Scrub 100ml giúp chăm sóc da bị mụn bởi chất nhờn trên da và những hạt mụn đáng ghét .\r\n\r\nGiúp quét sạch tế bào chết trong tích tắc trả lại cho bạn làn da mịn màng và khỏe mạnh hơn bao giờ hết.\r\n\r\nSản phẩm chứa hạt mịn tác động trực tiếp đến nang lông giúp thông thoáng lỗ chân lông, giảm mụn đầu đen và vết thâm do mụn, tạm biệt làn da cũ với làn da mới đầy mịn màng tươi mát.\r\n\r\nHướng dẫn sử dụng\r\nThoa gel rửa mặt Eucerin ProAcne Cleansing Gel lên mặt và massage nhẹ nhàng, rửa sạch lại với nước ấm. Tiếp tục sử dụng Nước hoa hồng Eucerin ProAcne Toner để làm sạch và cân bằng độ ẩm. Kết hợp dùng tẩy tế bào chết Eucerin ProAcne Scrub mỗi tuần 1 lần để làm sạch sâu lỗ chân lông và cải thiện bề mặt da.\r\n\r\nKhông nên kết hợp toner có chứa cồn cùng lúc với thuốc kháng sinh, thuốc trị mụn Isotretinoin hoặc có chứa hoạt chất Benzoyl Peroxide vì làn da sẽ trở nên mẫn cảm hơn. Thay vào đó, hãy dùng Eucerin ProAcne Acne & Make Up Cleansing Water (không chứa cồn) để làm sạ